declare const _default: "\nquery getArticles(\n    $bookUid: ID!\n    $q: String\n    $categoryUid: ID\n    $orderBy: String = \"createdAt\"\n    $first: Int\n    $offset: Int\n  ) {\n  node(id: $bookUid) {\n    ... on Book {\n      __typename\n      name\n      articles(\n          first: $first\n          offset: $offset\n          q: $q\n          categoryUid: $categoryUid\n          orderBy: $orderBy\n      ) {\n        totalCount\n        nodes {\n          uid\n          name\n          slug\n          id\n          bodyText\n          createdAt\n          updatedAt\n        }\n      }\n    }\n  }\n}\n";
export default _default;
